INSERT INTO `PLT_STATIC_CODE` VALUES ('221', 'TTTTTENANTID', 'PLT_SEQ_TEMPLATE', 'insertStatistic', 'INSERT INTO PLT_ORDER_STATISTIC (STATISTIC_DATE,TENANT_ID,CHANNEL_ID,ACTIVITY_SEQ_ID,SERVICE_TYPE,ORG_PATH,LOGIN_ID,IS_EXE,AREA_NO,CITY_ID,VALID_NUMS,VISIT_NUMS_TODAY,VISIT_NUMS_TOTAL,\r\nINTER_SUCCESS,VISITED_SUCCESS,VISITED_NO_SUCCESS,<#list contactType as code>TYPE${code},</#list><#list contactCode as code>ITEM${code},</#list>ITEM0)   \r\nSELECT NOW() STATISTIC_DATE,TENANT_ID,CHANNEL_ID,ACTIVITY_SEQ_ID,SERVICE_TYPE,ORDER_ASSIGNED_ORG_PATH ORG_PATH,ORDER_ASSIGNED_LOGIN_ID LOGIN_ID,\r\nIF(ORDER_ASSIGNED_LOGIN_ID IS NULL,\'0\',\'1\') IS_EXE, \r\nSUBSTRING_INDEX(SUBSTRING_INDEX(ORDER_ASSIGNED_ORG_PATH,\'/\',3),\'/\',-1) AREA_NO,\r\nSUBSTRING_INDEX(SUBSTRING_INDEX(ORDER_ASSIGNED_ORG_PATH,\'/\',4),\'/\',-1) CITY_ID,\r\nCOUNT(*) VALID_NUMS,\r\nCOUNT(IF(CONTACT_RESULT=1 AND DATE(CONTACT_DATE)=CURDATE(),TRUE,NULL)) VISIT_NUMS_TODAY,\r\nCOUNT(IF(CONTACT_RESULT=1 , TRUE, NULL)) VISIT_NUMS_TOTAL,\r\nCOUNT(IF(SUCCESS_STATUS=\'3\', TRUE, NULL)) INTER_SUCCESS,\r\nCOUNT(IF((CONTACT_RESULT=1 AND SUCCESS_STATUS=\'3\'), TRUE, NULL)) VISITED_SUCCESS,\r\nCOUNT(IF((CONTACT_RESULT=1 AND SUCCESS_STATUS<>\'3\'), TRUE, NULL)) VISITED_NO_SUCCESS,\r\n<#list contactType as code>\r\nIFNULL(COUNT(IF(CONTACT_TYPE=\'${code}\',TRUE,NULL)),0) TYPE${code}, \r\n</#list>\r\n<#list contactCode as code>\r\nIFNULL(COUNT(IF(CONTACT_CODE=\'${code}\',TRUE,NULL)),0) ITEM${code}, \r\n</#list>\r\nIFNULL(COUNT(IF(CONTACT_CODE IS NULL OR CONTACT_CODE=0,TRUE,NULL)),0) ITEM0  \r\nFROM ${tableName} WHERE TENANT_ID=\'${tenantId}\'  \r\nAND ACTIVITY_SEQ_ID=${activitySeqId}  \r\nAND CHANNEL_ID=\'${channelId}\' \r\n<#if orgPath??> AND ORDER_ASSIGNED_ORG_PATH LIKE \'${orgPath}%\'</#if>  \r\nAND ORDER_STATUS=\'5\'   \r\nGROUP BY TENANT_ID,CHANNEL_ID,ACTIVITY_SEQ_ID,SERVICE_TYPE,ORDER_ASSIGNED_ORG_PATH,ORDER_ASSIGNED_LOGIN_ID', '2017-06-22 09:44:14', '模板配置');
