INSERT INTO `PLT_STATIC_CODE` VALUES ('224', 'TTTTTENANTID', 'PLT_SEQ_TEMPLATE', 'trackStatistic', 'SELECT <#if bychannel> CHANNEL_ID, </#if> \r\n<#if benchList.validData??>\r\n	IFNULL(SUM(IF(ACTIVITY_SEQ_ID IN (<#list benchList.validData as code>${code?c},</#list> -1),CONTACT_NUM,0)),0) <#if bychannel> CONTACT_COUNT <#else> ALL_CONTACT_COUNT </#if>,\r\n	IFNULL(SUM(IF(ACTIVITY_SEQ_ID IN (<#list benchList.validData as code>${code?c},</#list> -1),VALUD_NUM-CONTACT_NUM-REMAIN_NUM,0)),0)  ALL_UN_CONTACT_COUNT,\r\n<#else>\r\n	IFNULL(SUM(0),0)  <#if bychannel> CONTACT_COUNT <#else> ALL_CONTACT_COUNT </#if>,IFNULL(SUM(0),0) ALL_UN_CONTACT_COUNT,\r\n</#if>\r\n<#if benchList.invalidData??>\r\n	IFNULL(SUM(IF(ACTIVITY_SEQ_ID IN (<#list benchList.invalidData as code>${code?c},</#list> -1),CONTACT_NUM,0)),0) <#if bychannel> VISITED_SUCCESS <#else> ALL_UN_SUCCESS_COUNT </#if>,\r\n	IFNULL(SUM(IF(ACTIVITY_SEQ_ID IN (<#list benchList.invalidData as code>${code?c},</#list> -1),VALUD_NUM-CONTACT_NUM-REMAIN_NUM,0)),0) PAST_UN_CONTACT_COUNT,\r\n<#else>\r\n	IFNULL(SUM(0),0) <#if bychannel> VISITED_SUCCESS <#else> ALL_UN_SUCCESS_COUNT </#if>,IFNULL(SUM(0),0) PAST_UN_CONTACT_COUNT,\r\n</#if>\r\nIFNULL(SUM(VALUD_NUM),0) <#if bychannel> ALL_COUNT <#else> ALL_VALID_COUNT </#if> ,\r\n<#if bychannel>\r\n<#if benchList.validData??>\r\nIFNULL(SUM(IF(ACTIVITY_SEQ_ID IN (<#list benchList.validData as code>${code?c},</#list> -1),VALUD_NUM,0)),0) VALID_NUM , \r\n<#else>\r\nIFNULL(SUM(0),0) VALID_NUM ,\r\n</#if>\r\n</#if>\r\nIFNULL(SUM(REMAIN_NUM),0) REMAIN_COUNT   \r\nFROM PLT_BENCH_STATISTIC  \r\nWHERE TENANT_ID=\'${tenantId}\' AND ACTIVITY_SEQ_ID IN (\r\n<#if benchList.validData??>\r\n<#list benchList.validData as code>${code?c},</#list>\r\n</#if>\r\n<#if benchList.invalidData??>\r\n<#list benchList.invalidData as icode>${icode?c},</#list>\r\n</#if>\r\n-1\r\n)  \r\n<#if bychannel> GROUP BY CHANNEL_ID </#if>', '2018-01-15 14:55:10', '模板配置');
