INSERT INTO `PLT_STATIC_CODE` VALUES ('227', 'TTTTTENANTID', 'PLT_SEQ_TEMPLATE', 'alertActivitySql', 'SELECT a.* FROM (\r\n<#list tableNames as orderTable>\r\nSELECT a.TENANT_ID tenantId,CONCAT(l.REC_ID,\'\') recId, a.ACTIVITY_ID activityId,\r\na.REC_ID activitySeqId, a.ACTIVITY_NAME activityName,  \r\na.ACTIVITY_THEMEID activityType, a.ACTIVITY_THEME activityTypeDesc, \r\na.ACTIVITY_LEVEL activityLevel, l.BEGIN_DATE beginDate, l.END_DATE endDate, \r\nl.PHONE_NUMBER phoneNum,  a.GROUP_NAME groupName , w.SMS_WORDS smsDesc,\r\nl.USER_ID userId, \'\' strategyInfo, w.TARGET target,  \r\na.STRATEGY_DESC policyDesc,  l.CONTACT_CODE contactCode,  \r\nw.CONTENT marketDesc FROM ${orderTable} l,PLT_ACTIVITY_INFO a,\r\nPLT_ACTIVITY_CHANNEL_DETAIL w \r\nWHERE l.TENANT_ID=\'${tenantId}\' AND l.CHANNEL_ID=\'${channelId}\' \r\nAND l.PHONE_NUMBER=\'${phoneNum}\'  AND l.ORDER_STATUS=\'5\' AND l.SUCCESS_STATUS<>\'3\' \r\nAND a.TENANT_ID=\'${tenantId}\' AND w.TENANT_ID=\'${tenantId}\'  AND l.CONTACT_CODE IN (\'0\',\'201\')  \r\nAND a.ACTIVITY_STATUS IN (\'1\',\'8\',\'9\') AND (l.CONTACT_NUM_DAY<w.NUMBERLIMIT OR l.CONTACT_NUM_DAY IS NULL )  \r\nAND a.REC_ID=l.ACTIVITY_SEQ_ID AND a.REC_ID=w.ACTIVITY_SEQ_ID AND l.CHANNEL_ID=w.CHANN_ID \r\n<#if orderTable_has_next>  UNION ALL  </#if>  \r\n</#list>\r\n) a LIMIT ${limit}', '2017-06-22 11:36:40', '模板配置');
