mapper.select:
SELECT ACTIVITY_ID activityId, CASE WHEN ACTIVITY_STATUS = '3' THEN '1' WHEN ACTIVITY_STATUS = '1' THEN '2' ELSE '3' END AS activityStatus, CASE WHEN ACTIVITY_STATUS = '3' THEN '活动工单生成中' WHEN ACTIVITY_STATUS = '1' THEN '活动工单已生成' ELSE '其他状态' END AS activityStatusDesc, DATE_FORMAT(LAST_ORDER_CREATE_TIME,'%Y%m%d%H%i%s') logTime FROM PLT_ACTIVITY_INFO WHERE TENANT_ID = #{parameterMap.tenantId} AND REC_ID in (SELECT MAX(REC_ID) FROM PLT_ACTIVITY_INFO WHERE ACTIVITY_ID in
#{item}
GROUP BY ACTIVITY_ID )
AND unix_timestamp(LAST_ORDER_CREATE_TIME) BETWEEN unix_timestamp(#{parameterMap.startTime}) AND unix_timestamp(#{parameterMap.endTime})
AND ACTIVITY_STATUS in
CASE WHEN #{item} = '1' THEN '3' WHEN #{item} = '2' THEN '1' ELSE null END
ORDER BY LAST_ORDER_CREATE_TIME ${parameterMap.orderBy}
LIMIT 0,#{parameterMap.size}

SELECT ACTIVITY_ID FROM PLT_ACTIVITY_INFO WHERE ACTIVITY_ID in
#{item}
AND TENANT_ID = #{parameterMap.tenantId}
