INSERT INTO `PLT_STATIC_CODE` VALUES ('TTTTTENANTID', 'PLT_SEQ_TEMPLATE', 'divideQuerySql', 'SELECT \r\n<#if type==\"chenckAll\"> \r\nREC_ID \r\n<#else>\r\n<#if type==\"count\">\r\nCOUNT(1) \r\n<#else>\r\nREC_ID,ACTIVITY_SEQ_ID ACTIVITY_ID,PHONE_NUMBER,AREA_NO,\r\n<#if fieldMaps.CITY_ID??>${fieldMaps.CITY_ID} AREA_NO,</#if>\r\n<#if fieldMaps.C_0051??>${fieldMaps.C_0051} TOWNS,</#if>\r\n<#if fieldMaps.CH_C_03??>${fieldMaps.CH_C_03} NET_CHANNEL,</#if>\r\nORDER_ASSIGNED_ORG_PATH S_ORG_PATH,ORDER_ASSIGNED_LOGIN_ID LOGIN_ID,\r\nCONCAT(ORDER_ASSIGNED_ORG_PATH,IF(ORDER_ASSIGNED_LOGIN_ID IS NOT NULL AND ORDER_ASSIGNED_LOGIN_ID<>\'\',\',\',\'\'),IF(ORDER_ASSIGNED_LOGIN_ID IS NOT NULL AND ORDER_ASSIGNED_LOGIN_ID<>\'\',ORDER_ASSIGNED_LOGIN_ID,\'\')) ORG_PATH\r\n</#if>\r\n</#if>\r\nFROM ${tableName} o\r\nWHERE TENANT_ID=\'${tenantId}\' \r\nAND ACTIVITY_SEQ_ID=${activitySeqId} ${reqParams.orderOrgSql}\r\nAND ORDER_ASSIGNED_ORG_PATH LIKE \'${reqParams.orgPath}%\'\r\nAND CHANNEL_ID=\'${channelId}\'\r\nAND ORDER_STATUS=\'5\'\r\nAND (CONTACT_CODE IS NULL OR CONTACT_CODE=0 ) \r\n<#if reqParams.phoneNum??> AND PHONE_NUMBER=\'${reqParams.phoneNum}\' </#if>\r\n<#if reqParams.userStatus??> AND ${fieldMaps.BA_S_01}=\'${reqParams.userStatus}\' </#if>\r\n<#if reqParams.rentFeeStart??> AND ${fieldMaps.LAST_1_FEE}>=\'${reqParams.rentFeeStart}\' </#if>\r\n<#if reqParams.rentFeeEnd??> AND ${fieldMaps.LAST_1_FEE}<=\'${reqParams.rentFeeEnd}\' </#if>\r\n<#if reqParams.onlineLongStart??> AND ${fieldMaps.ONLINE_DUR_LVL}/1>=\'${reqParams.onlineLongStart}\' </#if>\r\n<#if reqParams.onlineLongEnd??> AND ${fieldMaps.ONLINE_DUR_LVL}/1<=\'${reqParams.onlineLongEnd}\' </#if>\r\n<#if type==\"items\">LIMIT ${reqParams.pageSize*(reqParams.pageNum-1)},${reqParams.pageSize}</#if>', '2017-08-02 16:23:42', '模板配置');
