INSERT INTO `PLT_STATIC_CODE` VALUES ('TTTTTENANTID', 'PLT_SEQ_TEMPLATE', 'frontOrderQuery', '/*!mycat:sql=SELECT * FROM PLT_USER_LABEL WHERE TENANT_ID = \'${tenantId}\'*/\r\n<#if type==\"count\">SELECT SUM(a.total)</#if>\r\n<#if type==\"list\">\r\nSELECT IFNULL(b.TELPHONE,a.CONTACT_TELEPHONE_NO) CONTACT_TELEPHONE_NO,\r\nIFNULL(b.CUST_NAME,a.CUST_NAME) CUST_NAME,\r\nIFNULL(b.ADDRESS,a.KD_DETAIL_INSTALL_ADDRESS) KD_DETAIL_INSTALL_ADDRESS,\r\n<#if fieldMap.ONLINE_DUR_LVL??>CONVERT(a.ONLINE_DUR,CHAR) ONLINE_DUR,</#if> a.* \r\n</#if>\r\nFROM (\r\n<#list orderTables as tableName>\r\nSELECT \r\n<#if type==\"count\">\r\nCOUNT(1) total  \r\n<#elseif type==\"list\">\r\nTENANT_ID,\r\nREC_ID,\r\nACTIVITY_SEQ_ID ACTIVITY_ID,\r\nIF(CHANNEL_STATUS!=0 ,CONTACT_DATE,\'--\') ORDER_FINISH_DATE,\r\nCONTACT_TYPE ORDER_CONTACT_TYPE,\r\nCONTACT_CODE ORDER_RESULT_STATUS,\r\nCONTACT_RESULT,IFNULL(SUCCESS_STATUS,0) ORDER_STATUS,\r\nSERVICE_TYPE,\r\n<#if fieldMap.BA_S_01??>${fieldMap.BA_S_01} <#else> \'\' </#if>  USER_STATUS,\r\n<#if fieldMap.AREA_NO??>${fieldMap.AREA_NO} \'AREA_NO\',</#if>\r\n<#if fieldMap.CITY_ID??>${fieldMap.CITY_ID} CITY_ID,</#if>\r\n<#if fieldMap.CUST_NAME??>${fieldMap.CUST_NAME} <#else> \'\' </#if> CUST_NAME,\r\n<#if fieldMap.BA_N_11??>${fieldMap.BA_N_11} <#else> \'\' </#if> CONTACT_TELEPHONE_NO,\r\n<#if fieldMap.NUM_22??>${fieldMap.NUM_22} FIRST_MONTH_MOTH,</#if>\r\n<#if fieldMap.PRODUCT_NAME??>${fieldMap.PRODUCT_NAME} PACKAGE_NAME,</#if>\r\n<#if fieldMap.PR_R_01??>${fieldMap.PR_R_01} MIX_FLAG,</#if>\r\n<#if fieldMap.SERVICE_TYPE??>${fieldMap.SERVICE_TYPE} MOBILE_NET_TYPE,</#if>\r\n<#if fieldMap.CHR_46??>${fieldMap.CHR_46} CUST_TYPE,</#if>\r\n<#if fieldMap.W3_PROD_ID_1??>${fieldMap.W3_PROD_ID_1} ELECCHANNEL_FLAG,</#if>\r\n<#if fieldMap.RENT_FEE??>CASE WHEN ${fieldMap.RENT_FEE}>96 THEN \'是\' ELSE \'否\' END HIGH_FLAG,</#if>\r\n<#if fieldMap.PR_B_05??>${fieldMap.PR_B_05} PRODUCT_TYPE,</#if>\r\n<#if fieldMap.CH_C_03??>${fieldMap.CH_C_03} NETIN_CHANNEL,</#if>\r\n<#if fieldMap.ACTIVITY_TYPE??>${fieldMap.ACTIVITY_TYPE} AGREEMENT_TYPE,</#if>\r\n<#if fieldMap.CHR_14??>${fieldMap.CHR_14} AGREEMENT_NAME,</#if>\r\n<#if fieldMap.CHR_15??>${fieldMap.CHR_15} AGREEMENT_BEGIN_TIME,</#if>\r\n<#if fieldMap.NUM_1??>${fieldMap.NUM_1} AGREEMENT_REST_MONTHS,</#if>\r\n<#if fieldMap.EXP_DATE??>${fieldMap.EXP_DATE} AGREEMENT_EXPIRE_TIME,</#if>\r\n<#if fieldMap.JIAZHI_FLAG??>${fieldMap.JIAZHI_FLAG} JIAZHI_FLAG,</#if>\r\n<#if fieldMap.TE_N_02??>${fieldMap.TE_N_02} TERMINAL_BRAND,</#if>\r\n<#if fieldMap.TE_N_04??>${fieldMap.TE_N_04} NET_TYPE,</#if>\r\n<#if fieldMap.ONLINE_DUR_LVL??&&fieldMap.LAST3_FLUX??>CASE WHEN ${fieldMap.ONLINE_DUR_LVL}=0 or ${fieldMap.ONLINE_DUR_LVL} IS NULL THEN ${fieldMap.LAST3_FLUX} WHEN ${fieldMap.ONLINE_DUR_LVL}>=3 THEN ${fieldMap.LAST3_FLUX}/3 when ${fieldMap.ONLINE_DUR_LVL}<3 then ${fieldMap.LAST3_FLUX}/${fieldMap.ONLINE_DUR_LVL} END AVERAGE_FLOW,</#if>\r\n<#if fieldMap.NUM_21??>${fieldMap.NUM_21} BALANCE_FEE,</#if>\r\n<#if fieldMap.ONLINE_DUR_LVL??>CONVERT(${fieldMap.ONLINE_DUR_LVL},DECIMAL(10,0)) ONLINE_DUR,</#if>\r\n<#if fieldMap.ACCT_FEE??>CONVERT(${fieldMap.ACCT_FEE},DECIMAL(10,0)) ARPU_VALUE,</#if>\r\n<#if fieldMap.RENT_FEE??>${fieldMap.RENT_FEE} RENT_FEE,</#if>\r\n<#if fieldMap.KD_CHR_2??>${fieldMap.KD_CHR_2} KD_FIRST_MONTH_MOTH,</#if>\r\n<#if fieldMap.KD_DQ_MONTH??>${fieldMap.KD_DQ_MONTH} PACKAGE_EXPIRE_TIME,</#if>\r\n<#if fieldMap.KD_IS_KDDQXQ??>${fieldMap.KD_IS_KDDQXQ} CONTINUE_AGREEMENT_FLAG,</#if>\r\n<#if fieldMap.KD_XQ_DATE??>${fieldMap.KD_XQ_DATE} CONTINUE_AGREEMENT_TIME,</#if>\r\n<#if fieldMap.KD_SERVICE_SUB_TYPE??>${fieldMap.KD_SERVICE_SUB_TYPE} SUB_PACKAGE_NAME,</#if>\r\n<#if fieldMap.KD_COMB_SUB_TYPE??>${fieldMap.KD_COMB_SUB_TYPE} BRAND_MIX_TYPE,</#if>\r\n<#if fieldMap.KD_COMB_FX_NUMBER??>${fieldMap.KD_COMB_FX_NUMBER} MIX_FIX_PHONE_NO,</#if>\r\n<#if fieldMap.KD_BILLING_BANDWIDTH??>${fieldMap.KD_BILLING_BANDWIDTH} CUR_RATE,</#if>\r\n<#if fieldMap.KD_MANAGER_NAME??>${fieldMap.KD_MANAGER_NAME} OWNER_AREA,</#if>\r\n<#if fieldMap.KD_ADDR_SIX_NAME??>${fieldMap.KD_ADDR_SIX_NAME} BRAND_ADDRESS,</#if>\r\n<#if fieldMap.CH_C_03??>${fieldMap.CH_C_03} NETIN_HALL,</#if>\r\n<#if fieldMap.KD_MANAGER_NAME??>${fieldMap.KD_MANAGER_NAME} CUST_MANAGER,</#if>\r\n<#if fieldMap.KD_PRODUCT_ID??>${fieldMap.KD_PRODUCT_ID} KD_PACKAGE_NAME,</#if>\r\n<#if fieldMap.KD_COMB_START_DATE??>IF(DATE_FORMAT(${fieldMap.KD_COMB_START_DATE},\'%Y-%m-%d %H:%i:%s\') IS NOT NULL,DATE_FORMAT(${fieldMap.KD_COMB_START_DATE},\'%Y-%m-%d %h:%i:%s\'),\'--\') BRAND_MIX_BEGIN_TIME,</#if>\r\n<#if fieldMap.KD_MOBILE_NUMBER1??>${fieldMap.KD_MOBILE_NUMBER1} MIX_MOBILE_PHONE,</#if>\r\n<#if fieldMap.KD_BALANCE_FEE??>${fieldMap.KD_BALANCE_FEE} KD_BALANCE_FEE,</#if>\r\n<#if fieldMap.OWE_FEE??>IF(${fieldMap.OWE_FEE}>0,\'是\',\'否\') \'OWE_FLAG\',</#if>\r\n<#if fieldMap.OWE_FEE??>${fieldMap.OWE_FEE} \'OWE_FEE\',</#if>\r\n<#if fieldMap.KD_NUM_2??>${fieldMap.KD_NUM_2} KD_OWE_FEE,</#if>\r\n<#if fieldMap.KD_INNET_LENGTH??>${fieldMap.KD_INNET_LENGTH} KD_ONLINE_DUR,</#if>\r\n<#if fieldMap.KD_DETAIL_INSTALL_ADDRESS??>${fieldMap.KD_DETAIL_INSTALL_ADDRESS} <#else> \'\' </#if> KD_DETAIL_INSTALL_ADDRESS,\r\n<#if fieldMap.KD_IS_IPTV??>${fieldMap.KD_IS_IPTV} KD_IS_IPTV,</#if>\r\nUSER_ID,\r\nPHONE_NUMBER\r\n</#if>\r\nFROM ${tableName}\r\nWHERE TENANT_ID=\'${tenantId}\' AND ACTIVITY_SEQ_ID IN (\r\n<#list activitySeqs as seq>\r\n${seq.ACTIVITY_ID?c}\r\n<#if seq_has_next>,</#if>\r\n</#list>\r\n)\r\n<#list reqParams.pama?values as value>\r\nAND ${value}  \r\n</#list>\r\nAND ORDER_STATUS=\'5\'\r\n<#if reqParams.todayTime??&&reqParams.todayTime!=\"\">  AND DATE_FORMAT(CONTACT_DATE,\'%Y%m%d\')=\'${reqParams.todayTime}\'  </#if> \r\nAND CHANNEL_ID=\'${channelId}\'\r\n<#if reqParams.roleType==\"1\">\r\nAND ORDER_ASSIGNED_LOGIN_ID = \'${reqParams.loginId}\' \r\n<#else>\r\nAND (ORDER_ASSIGNED_ORG_PATH LIKE \'${reqParams.orgPath}/%\' OR ORDER_ASSIGNED_ORG_PATH=\'${reqParams.orgPath}\') \r\n</#if>\r\n<#if tableName_has_next>  UNION ALL  </#if>   \r\n</#list>\r\n)